import "../styles/Filters.scss";

function Filters(props) {
  const getYears = () => {
    const yearMovies = props.movieScene.map((movie) => movie.year);
    const uniqueYear = yearMovies.filter((movie, i) => {
      return yearMovies.indexOf(movie) === i;
    });
    return uniqueYear;
  };
  const year = uniqueYear.map((scene, i) => {
    return (
      <option key={i} value={scene.year}>
        {`year ${scene.year}`}
      </option>
    );
  });
  return (
    <form className="Filters">
      <label htmlFor="inputSearch">Search movie</label>
      <input
        className="search"
        autoComplete="off"
        type="search"
        name="inputSearch"
        placeholder="Filter movies"
      />
      <label htmlFor="year">Filter by year</label>
      <select id="year">
        <option value="All">All</option>
        {getYears()}
      </select>
    </form>
  );
}

export default Filters;
